import React, { useState } from 'react';
import VisualPage, {
  About,
  Complexity,
  Controls,
  ControlGroup,
  Visualization,
} from '../VisualPage.js';
import './VisualComponents.css';

export function LinkedList(props) {
  return (
    <div className="linked-list">
      {props.children}
    </div>
  );
}

function LinkedListNode(props) {
  return (
    <div className={'linked-list-node' + (props.highlight ? ' highlight' : '')}>
      {props.children}
    </div>
  );
}

function Demo() {
  const [list, setList] = useState([]);
  const [start, setStart] = useState(-1);
  const [end, setEnd] = useState(-1);
  const [addVal, setAddVal] = useState();
  const [searchVal, setSearchVal] = useState();
  const [removeVal, setRemoveVal] = useState();

  function add(addEnd) {
    let newList = list.map(v => Object.assign({}, v, {
      "highlight": false,
    }));
    let added = false;
    const newItem = {
      "next": addEnd ? -1 : start,
      "value": addVal,
      "highlight": false,
    }
    // Add new item in next undefined slot
    let i = 0;
    for (; i < newList.length && !added; i++) {
      if (newList[i] === undefined) {
        newList[i] = newItem;
        added = true;
      }
    }
    if (!added) {
      i = newList.length;
      newList.push(newItem);
    }

    // Fix old last node's pointer if addEnd
    if (addEnd) {
      if (end === -1) {
        setEnd(i);
      } else {
        newList[end].next = i;
        setEnd(i);
      }

      if (start === -1)
        setStart(i);
    } else {
      setStart(i);
      if (end === -1)
        setEnd(i);
    }
    setList(newList);
  }


  function search() {
    setList(list.map((v, i) => Object.assign({}, v, {
      "highlight": false,
    })));

    let index = start;
    let visted = [];

    let cancel = setInterval(() => {
      if (index === -1) {
        setList(list.map((v, i) => Object.assign({}, v, {
          "highlight": visted.includes(i)
            && v.value === searchVal,
        })));
        clearInterval(cancel)
        return;
      }
      setList(list.map((v, i) => Object.assign({}, v, {
        "highlight": i === index
          || (v.value === searchVal && visted.includes(i)),
      })));
      visted.push(index);

      // If found item stop
      if (index !== -1 && list[index].value === searchVal) {
        index = -1;

      } else {
        index = list[index].next;
      }
    }, 1000);



  }

  //make em global
  var idx = null;
  var found = false;

  function searchRemove(searchVal) {
    setList(list.map((v, i) => Object.assign({}, v, {
      "highlight": false,
    })));

    let index = start;
    let visted = [];

    let cancel = setInterval(() => {
      if (index === -1) {
        setList(list.map((v, i) => Object.assign({}, v, {
          "highlight": visted.includes(i)
            && v.value === searchVal,
        })));
        clearInterval(cancel)
        return;
      }
      setList(list.map((v, i) => Object.assign({}, v, {
        "highlight": i === index
          || (v.value === searchVal && visted.includes(i)),
      })));
      visted.push(index);

      // If found item stop
      if (index !== -1 && list[index].value === searchVal) {
        found = true
        idx = index
        index = -1;
        console.log("Found")
        //clearInterval(cancel)
      } else {
        index = list[index].next;
      }
    }, 500);



  }


  function remove() {
    searchRemove(removeVal)
    setTimeout(function () {
      // console.log(found, idx)
      if (found === false) {
        alert("Value: " + removeVal + " is not in the list")
      } else {
        //found something. Kill it
        var nl = Object.assign([], list)
        var prev = null //prev pointer
        var index = start;
        while (index !== idx) {
          prev = index
          index = list[index].next
          console.log("Here "+ prev + " " + index)
        }
        console.log("tHere "+ prev + " " + index)

        if (prev === null && nl[index].next === -1) {
          //only one element
          setStart(-1)
          setList([])

        } else if(prev!==null){
          //not first element
          var next = nl[index].next //next pointer
          nl[prev].next = next
          console.log(nl)
          setList(nl)
        }else{
          //first element
          var nxt = nl[index].next //next pointer
          //nl[index].next = nxt
          setStart(nxt)
          nl.splice(index,1)
          console.log(nl)
          setList(nl)
        }

      }

    }, 2000);
  }


  // Generate the nodes in correct order for visualization
  const ordered = [];
  let i = start;
  while (i !== -1) {
    ordered.push(
      <LinkedListNode key={i} highlight={list[i].highlight}>{list[i].value}</LinkedListNode>
    );
    i = list[i].next;
  }
  return (
    <>
      <Controls>
        <ControlGroup>
          <label htmlFor="add">Add item</label>
          <input name="add" type="text" onChange={e => setAddVal(e.target.value)}></input>
          <button onClick={() => add(false)}>Add Front</button>
          <button onClick={() => add(true)}>Add End</button>
        </ControlGroup>
        <ControlGroup>
          <label htmlFor="search">Search</label>
          <input name="search" onChange={e => setSearchVal(e.target.value)}></input>
          <button onClick={search}>Search</button>
        </ControlGroup>
        <ControlGroup>
          <label htmlFor="remove">Remove</label>
          <input name="remove" onChange={e => setRemoveVal(e.target.value)}></input>
          <button onClick={remove}>Remove</button>
        </ControlGroup>
      </Controls>
      <Visualization>
        <LinkedList>
          {ordered}
        </LinkedList>
      </Visualization>
    </>
  );
}

export default function LinkedListPage(props) {
  return (
    <VisualPage title="Linked List">
      <About>
        <h4>What is a Linked List?</h4>
        Linked List is a linear data structure and it is very common data structure which consists of group of nodes in a sequence which is divided in two parts. Each node consists of its own data and the address of the next node and forms a chain.
      </About>
      <Complexity complexity={[
        {
          "name": "Indexing",
          "complexity": "Î˜(n)"
        },
        {
          "name": "Insert/delete at beginning",
          "complexity": "Î˜(1)"
        },
        {
          "name": "Insert/delete at end (when last element known)",
          "complexity": "Î˜(1)"
        },
        {
          "name": "Insert/delete in middle",
          "complexity": "Î˜(n) + Î˜(1)",
        },
        {
          "name": "Average wasted space",
          "complexity": "Î˜(1)",
        },
      ]} />
      <Demo />
    </VisualPage>
  );
}
