import React, { useState } from 'react';
import VisualPage, {
  About,
  Complexity,
  Controls,
  ControlGroup,
  Visualization,
} from '../VisualPage.js';
import {
  Node,
} from '../VisualComponents.js';


function Demo() {
  const [list, setList] = useState([]);
  const [start, setStart] = useState(-1);
  const [end, setEnd] = useState(-1);
  const [addVal, setAddVal] = useState();
  const [searchVal, setSearchVal] = useState();
  const [removeVal, setRemoveVal] = useState();

  function add(addEnd) {
    let newList = list.map(v => Object.assign({}, v, {
      "highlight": false,
    }));
    let added = false;
    const newItem = {
      "next": addEnd ? -1 : start,
      "value": addVal,
      "highlight": false,
    }
    // Add new item in next undefined slot
    let i = 0;
    for (; i < newList.length && !added; i++) {
      if (newList[i] === undefined) {
        newList[i] = newItem;
        added = true;
      }
    }
    if (!added) {
      i = newList.length;
      newList.push(newItem);
    }

    // Fix old last node's pointer if addEnd
    if (addEnd) {
      if (end === -1) {
        setEnd(i);
      } else {
        newList[end].next = i;
        setEnd(i);
      }

      if (start === -1)
        setStart(i);
    } else {
      setStart(i);
      if (end === -1)
        setEnd(i);
    }
    setList(newList);
  }


  function search(searchVal) {
    setList(list.map((v, i) => Object.assign({}, v, {
      "highlight": false,
    })));

    let index = start;
    let visted = [];

    let cancel = setInterval(() => {
      // End of list and not found
      if (index === -1) {
        setList(list.map((v, i) => Object.assign({}, v, {
          "highlight": false
        })));
        clearInterval(cancel)
        return -1;
      }
      setList(list.map((v, i) => Object.assign({}, v, {
        "highlight": i === index
          || (v.value === searchVal && visted.includes(i)),
      })));
      visted.push(index);

      // If found item stop
      if (index !== -1 && list[index].value === searchVal) {
        setList(list.map((v, i) => Object.assign({}, v, {
          "highlight": i === index
        })));
        return index;
      } else {
        index = list[index].next;
      }
    }, 1000);
  }

  function remove() {
    let index = search(removeVal)
    if (index === -1) {
      // Not found
    } else {
      // Removal animation
      let newList = list.slice();
      let finished = false;
      for (let i = 0; !finished; newList[i].next) {
        if (newList[i].next === index) {
          // Next node is removal node so remove it
          newList[i].next = newList[newList[i].next].next;
