import React, { useState, useEffect } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import VisualPage, {
  About,
  Complexity,
  Controls,
  ControlGroup,
  Visualization,
} from '../VisualPage.js';
import './Styles/queue.css';

function Queue(props) {
  return (
    <TransitionGroup className="queue">
      {props.children.map((node, i) => {
        return (
          <CSSTransition key={i} timeout={200} classNames="queue-node">
            {node}
          </CSSTransition>
        );
      })}
    </TransitionGroup>
  );
}

function QueueNode(props) {
  return (
    <div className={"queue-node" + (props.highlight ? " highlight" : "")}>
      {props.children}
    </div>
  );
}

function Demo() {
  const [list, setList] = useState([]);
  const [addVal, setAddVal] = useState();

  function add() {
    setList([
      ...list,
      {
        "value": addVal,
        "highlight": false,
      }
    ]);
  }

  function remove() {
    setList(list.slice(1));
  }

  // Generate the nodes in correct order for visualization
  return (
    <>
      <Controls>
        <ControlGroup>
          <label htmlFor="add">Add item</label>
          <input name="add" type="text" onChange={e => setAddVal(e.target.value)}></input>
          <button onClick={add}>Enqueue</button>
        </ControlGroup>
        <ControlGroup>
          <button onClick={remove}>Dequeue</button>
        </ControlGroup>
      </Controls>
      <Visualization>
        <Queue>
          {list.map((node, i) => {
            return (
              <QueueNode key={i} index={i} highlight={node.highlight}>
                {node.value}
              </QueueNode>
            );
          })}
        </Queue>
      </Visualization>
    </>
  );
}

export default function QueuePage(props) {
  return (
    <VisualPage title="Queue">
      <About>
        <h4>What is a Queue?</h4>
        Stacks are a FIFO (first in- first out) structure. They are used in a lot of reactive and UI related
        tasks, think back button on browsers
      </About>
      <Complexity complexity={[
        {
          "name": "Indexing",
          "complexity": "Θ(n)"
        },
        {
          "name": "Enqueue/Dequeue Element",
          "complexity": "Θ(1)"
        },
        {
          "name": "Average wasted space",
          "complexity": "Θ(1)",
        },
      ]} />
      <Demo />
    </VisualPage>
  );
}
