import React, { useState } from 'react';
import VisualPage, {
  About,
  Complexity,
  Controls,
  ControlGroup,
  Visualization,
} from '../VisualPage.js';
import {
  Node,
} from '../VisualComponents.js';

function Demo() {
  const [list, setList] = useState([]);
  const [start, setStart] = useState(-1);
  const [end, setEnd] = useState(-1);
  const [val, setVal] = useState();

  function add(addEnd) {
    let newList = list.slice();
    let added = false;
    const newItem = {
      "next": addEnd ? -1 : start,
      "value": val,
      "hihlight": false,
    }

    // Add new item in next undefined slot
    for (let i = 0; i < newList.length && !added; i++) {
      if (newList[i] === undefined) {
        newList[i] = newItem;
        added = true;
      }
    }
    if (!added) {
      newList.append(newItem);
    }

    // Fix old last node's pointer if addEnd
    if (addEnd) {

    }
    setList(newList);
  }

  function addEnd() {
    setList([
      ...list,
      {
        "value": val,
        "highlight": true,
      },
    ]);
  }

  return (
    <>
      <Controls>
        <ControlGroup>
          <label htmlFor="add">Add item</label>
          <input name="add" type="text" onChange={e => setVal(e.target.value)}></input>
          <button onClick={addStart}>Add Front</button>
          <button onClick={addEnd}>Add End</button>
        </ControlGroup>
      </Controls>
      <Visualization>
        {list.map((v,i) => <Node key={i} highlight={v.highlight}>{v.value}</Node>)}
      </Visualization>
    </>
  );
}

export default function LinkedList(props) {
  return (
    <VisualPage title="Linked List">
      <About>
        <h4>What is a Linked List?</h4>
        Linked List is a linear data structure and it is very common data structure which consists of group of nodes in a sequence which is divided in two parts. Each node consists of its own data and the address of the next node and forms a chain.
      </About>
      <Complexity complexity={[
        {
          "name": "Indexing",
          "complexity": "Θ(n)"
        },
        {
          "name": "Insert/delete at beginning",
          "complexity": "Θ(1)"
        },
        {
          "name": "Insert/delete at end (when last element known)",
          "complexity": "Θ(1)"
        },
        {
          "name": "Insert/delete in middle",
          "complexity": "Θ(n) + Θ(1)",
        },
        {
          "name": "Average wasted space",
          "complexity": "Θ(1)",
        },
      ]}/>
      <Demo />
    </VisualPage>
  );
}
